package com.megha.godsendapp

import android.content.Intent
import android.support.v7.app.AppCompatActivity
import android.os.Bundle
import android.widget.ImageView
import com.smarteist.autoimageslider.IndicatorAnimations
import com.smarteist.autoimageslider.SliderAnimations
import com.smarteist.autoimageslider.SliderLayout
import com.smarteist.autoimageslider.SliderView
import com.smarteist.autoimageslider.DefaultSliderView
import android.widget.Toast
import kotlinx.android.synthetic.main.activity_main.*


class MainActivity : AppCompatActivity() {
    var sliderLayout: SliderLayout? = null
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)
         sliderLayout = findViewById<SliderLayout>(R.id.imageSlider);
        sliderLayout!!.setIndicatorAnimation(IndicatorAnimations.SWAP)
        sliderLayout!!.setSliderTransformAnimation(SliderAnimations.FADETRANSFORMATION)//set indicator animation by using SliderLayout.Animations. :WORM or THIN_WORM or COLOR or DROP or FILL or NONE or SCALE or SCALE_DOWN or SLIDE and SWAP!!
        sliderLayout!!.scrollTimeInSec = 1; //set scroll delay in seconds :

        setSliderViews()
       // onBackPressed()

        //navigating in activites
        home.setOnClickListener {
            var intenthome=Intent(this,MainActivity::class.java)
            startActivity(intenthome)
            finish()
        }
        objective.setOnClickListener {
            var intentobjective=Intent(this,ObjectiveActivity::class.java)
            startActivity(intentobjective)
            finish()
        }
        members.setOnClickListener {
            var intentmembers=Intent(this,MembersActivity::class.java)
            startActivity(intentmembers)
            finish()

        }
        events.setOnClickListener {
            var intentevents=Intent(this,EventsActivity::class.java)
            startActivity(intentevents)
            finish()


        }
        contact.setOnClickListener {
            var intentcontact=Intent(this,ContactActivity::class.java)
            startActivity(intentcontact)

        }
    }

