fun setSliderViews(){
    for (i in 0..2){
        val sliderView = DefaultSliderView(this)
        when (i) {
            0 -> sliderView.imageUrl="data:image/jpeg;base64,"
           1-> sliderView.imageUrl="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQCIzz6BtpPngJduiqYXZkLiVl-jjSTCNA7_evs55CQ7hGuL8rgVw"
            // 1 -> sliderView.imageUrl = "http://3.imimg.com/data3/FP/OB/GLADMIN-24784/charitable-trust-250x250.jpg"
            2 -> sliderView.imageUrl = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQsXw5VAlMVqFjkBctVLOvEziyWHXmiFHU0buAFeUf3iSGhraGIkA"
          //  3 -> sliderView.imageUrl = "https://images.pexels.com/photos/929778/pexels-photo-929778.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260"
        }
        sliderView.setImageScaleType(ImageView.ScaleType.FIT_XY)

   //     sliderView.setOnSliderClickListener { Toast.makeText(this@MainActivity, "This is slider ", Toast.LENGTH_SHORT).show() }
        sliderLayout!!.addSliderView(sliderView)
    }
}

